import { connect } from "react-redux";
import ProductDetails from '../components/ProductDetails'
import getProductDetails from "../redux/actions/getProductDetails";

const mapDispatchToProps = dispatch => {
    return{
        getProductDetails: () => dispatch(getProductDetails())
    }
}

const mapStateToProps = state => {
    const {loading, selectedProduct, error} = state
    return{
        loading,
        error,
        selectedProduct
    }
}

const ProductDetailsContainer = connect(mapDispatchToProps, mapStateToProps)(ProductDetails);

export default ProductDetailsContainer;
