import { GET_PRODUCT_DETAILS } from '../actions/actionTypes';

const initialState = {
    selectedproduct:''
}

const productDetails = (state = initialState, action) => {
    switch(action.type){
        case GET_PRODUCT_DETAILS:
            return {
                selectedproduct: action.payload.productId
            }
        default:
            return state
    }
}

export default productDetails;
