export default class ProductList extends React.Component{
    componentDidMount(){
        this.props.getAllProducts();
    }
    render(){
        <ul className='productList'>
            {this.props.products.map((elem, index) => {
                <Product key={index} name={elem.name} />
            })}
        </ul>
    }
}
